Commandcheckbox.OnLeftClick:
if ${This.Checked}
{
	Script[EQ2Bot].Variable[DebuffMode]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Cast Debuff Spells",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[DebuffMode]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Cast Debuff Spells",FALSE]:Save
}

if ${This.Checked}
{
	Script[EQ2Bot].Variable[AoEMode]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Cast AoE Spells",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[AoEMode]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Cast AoE Spells",FALSE]:Save
}

			if ${This.Checked}
			{

				Script[EQ2Bot].Variable[SurroundingAttacksMode]:Set[TRUE]
				SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Buff Surrounding Attacks",TRUE]:Save
			}
			else
			{

				Script[EQ2Bot].Variable[SurroundingAttacksMode]:Set[FALSE]
				SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Buff Surrounding Attacks",FALSE]:Save
			}

if ${This.Checked}
{
	Script[EQ2Bot].Variable[UseRangeMode]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Cast Range Arts",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[UseRangeMode]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Cast Range Arts",FALSE]:Save
}

if ${This.Checked}
{
	Script[EQ2Bot].Variable[CloakMode]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Stealth After Combat",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[CloakMode]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Stealth After Combat",FALSE]:Save
}

combobox.OnLeftClick:
declare Counter int
Counter:Set[1]
This:ClearItems
Me:CreateCustomInventoryArray[nonbankonly]
do
{
	if ${Me.CustomInventory[${Counter}].Type.Equal[Weapon]}
	{
		This:AddItem[${Me.CustomInventory[${Counter}].Name}]
	}
}
while ${Counter:Inc}<=${Me.CustomInventoryArraySize}
Counter:Set[1]
do
{
	if ${Me.Equipment[${Counter}].Type.Equal[Weapon]}
	{
		This:AddItem[${Me.Equipment[${Counter}].Name}]
	}
}
while ${Counter:Inc}<=22

declare Counter int
Counter:Set[1]
This:ClearItems
Me:CreateCustomInventoryArray[nonbankonly]
do
{
	if ${Me.CustomInventory[${Counter}].Type.Equal[Shield]} || ${Me.CustomInventory[${Counter}].Type.Equal[Weapon]}
	{
		This:AddItem["${Me.CustomInventory[${Counter}].Name}"]
	}
}
while ${Counter:Inc}<=${Me.CustomInventoryArraySize}
Counter:Set[1]
do
{
	if ${Me.Equipment[${Counter}].Type.Equal[Shield]} || ${Me.Equipment[${Counter}].Type.Equal[Weapon]}
	{
		This:AddItem["${Me.Equipment[${Counter}].Name}"]
	}
}
while ${Counter:Inc}<=22

