if ${This.Checked}
{
	Script[EQ2Bot].Variable[StartHO]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Start HOs",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[StartHO]:Set[FALSE]
