Commandcheckbox.OnLeftClick:
if ${This.Checked}
{
	Script[EQ2Bot].Variable[AoEMode]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Cast AoE Spells",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[AoEMode]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Cast AoE Spells",FALSE]:Save
}

if ${This.Checked}
{
	Script[EQ2Bot].Variable[PBAoEMode]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Cast PBAoE Spells",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[PBAoEMode]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Cast PBAoE Spells",FALSE]:Save
}

if ${This.Checked}
{
	Script[EQ2Bot].Variable[BuffDamageShield]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Buff Damage Shield",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[BuffDamageShield]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Buff Damage Shield",FALSE]:Save
}

combobox.Width:
100
		20
		The Group or Raid Member to Damage Shield Buff
		
		
			This:AddItem[${SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}].GetString[BuffDamageShieldGroupMember]}]
			This.ItemByValue[${SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}].GetString[BuffDamageShieldGroupMember]}]:Select
		
		
			SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set[BuffDamageShieldGroupMember,${This.SelectedItem.Text}]:Save
		
			declare tmpvar int

			This:ClearItems
			tmpvar:Set[1]

			if ${Me.InRaid}
			{
				do
				{
					if ${Me.Raid[${tmpvar}](exists)}
					{
						This:AddItem[${Me.Raid[${tmpvar}].Name}:PC]
					}
				}
				while ${tmpvar:Inc}<=24
			}
			else
			{
				This:AddItem[${Me.Name}:${Me.ToActor.Type}]

				if ${Me.ToActor.Pet(exists)}
				{
					This:AddItem[${Me.ToActor.Pet}:${Me.ToActor.Pet.Type}]
				}

				if ${Me.Grouped}
				{

					tmpvar:Set[1]
					do
					{
							if ${Me.Group[${tmpvar}].ToActor(exists)}
							{
								This:AddItem[${Me.Group[${tmpvar}].Name}:${Me.Group[${tmpvar}].ToActor.Type}]
							}
							if (${Me.Group[${tmpvar}].Class.Equal[conjuror]} || ${Me.Group[${tmpvar}].Class.Equal[necromancer]}) && ${Me.Group[${tmpvar}].ToActor.Pet(exists)}
							{
								This:AddItem[${Me.Group[${tmpvar}].ToActor.Pet}:${Me.Group[${tmpvar}].ToActor.Pet.Type}]
							}
					}
					while ${tmpvar:Inc}<=5
				}
			}

