combobox.OnLeftClick:
declare Counter int
Counter:Set[1]
This:ClearItems
Me:CreateCustomInventoryArray[nonbankonly]
do
{
	if ${Me.CustomInventory[${Counter}].Type.Equal[Weapon]}
	{
		This:AddItem[${Me.CustomInventory[${Counter}].Name}]
	}
}
while ${Counter:Inc}<=${Me.CustomInventoryArraySize}
Counter:Set[1]
do
{
	if ${Me.Equipment[${Counter}].Type.Equal[Weapon]}
	{
		This:AddItem[${Me.Equipment[${Counter}].Name}]
	}
}
while ${Counter:Inc}<=22

declare Counter int
Counter:Set[1]
This:ClearItems
Me:CreateCustomInventoryArray[nonbankonly]
do
{
	if ${Me.CustomInventory[${Counter}].Type.Equal[Shield]} || ${Me.CustomInventory[${Counter}].Type.Equal[Symbol]}
	{
		This:AddItem[${Me.CustomInventory[${Counter}].Name}]
	}
}
while ${Counter:Inc}<=${Me.CustomInventoryArraySize}
Counter:Set[1]
do
{
	if ${Me.Equipment[${Counter}].Type.Equal[Shield]} || ${Me.Equipment[${Counter}].Type.Equal[Symbol]}
	{
		This:AddItem[${Me.Equipment[${Counter}].Name}]
	}
}
while ${Counter:Inc}<=22

declare Counter int
Counter:Set[1]
This:ClearItems
Me:CreateCustomInventoryArray[nonbankonly]
do
{
	if ${Me.CustomInventory[${Counter}].SubType.Equal[Staff]} && ${Me.CustomInventory[${Counter}].WieldStyle.Find[Two-Handed]}
	{
		This:AddItem[${Me.CustomInventory[${Counter}].Name}]
	}
}
while ${Counter:Inc}<=${Me.CustomInventoryArraySize}
