Commandcheckbox.OnLeftClick:
if ${This.Checked}
{
	Script[EQ2Bot].Variable[MezzMode]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Mezz Mode",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[MezzMode]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Mezz Mode",FALSE]:Save
}

if ${This.Checked}
{
	Script[EQ2Bot].Variable[ConvertMode]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Start HOs",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[ConvertMode]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Start HOs",FALSE]:Save
}

if ${This.Checked}
{
	Script[EQ2Bot].Variable[SummonImpOfRo]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Summon Imp of Ro",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[SummonImpOfRo]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Summon Imp of Ro",FALSE]:Save
}

combobox.Width:
100
		20
		The Raid Friend to cast the 3-proc "Prismatic" buff upon.
		
		Text
        
		    if (${SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}].GetString[BuffPrism](exists)})
		    {
    			This:AddItem[${SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}].GetString[BuffPrism]}]
    			This.ItemByValue[${SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}].GetString[BuffPrism]}]:Select
    		}
    		else
    		{
    		    This:AddItem["No one"]
    		    This.ItemByValue["No one"]:Select
    		}
                		
		
			SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set[BuffPrism,${This.SelectedItem.Text}]:Save
		
			declare tmpvar int

			This:ClearItems
			tmpvar:Set[1]
			This:AddItem["No one"]
			This:AddItem[${Me.Name}:${Me.ToActor.Type}]
			if ${Me.Pet(exists)}
			{
				This:AddItem[${Me.Pet.Name}:${Me.Pet.Type}]
			}
			if ${Me.Raid} > 0
			{
			    do
			    {
					if ${Me.Raid[${tmpvar}].ToActor(exists)}
					{
						This:AddItem[${Me.Raid[${tmpvar}].Name}:${Me.Raid[${tmpvar}].ToActor.Type}]
					}
					if (${Me.Raid[${tmpvar}].Class.Equal[conjuror]} || ${Me.Raid[${tmpvar}].Class.Equal[necromancer]})  && ${Me.Raid[${tmpvar}].ToActor.Pet(exists)}
					{
						This:AddItem[${Me.Raid[${tmpvar}].ToActor.Pet}:${Me.Raid[${tmpvar}].ToActor.Pet.Type}]
					}
			    }
			    while ${tmpvar:Inc}< ${Me.Raid}			
			}
			elseif ${Me.Group} > 1
			{
			    do
			    {
					if ${Me.Group[${tmpvar}].ToActor(exists)}
					{
						This:AddItem[${Me.Group[${tmpvar}].Name}:${Me.Group[${tmpvar}].ToActor.Type}]
					}
					if (${Me.Group[${tmpvar}].Class.Equal[conjuror]} || ${Me.Group[${tmpvar}].Class.Equal[necromancer]})  && ${Me.Group[${tmpvar}].ToActor.Pet(exists)}
					{
						This:AddItem[${Me.Group[${tmpvar}].ToActor.Pet}:${Me.Group[${tmpvar}].ToActor.Pet.Type}]
					}
			    }
			    while ${tmpvar:Inc}< ${Me.Group}
			}
			This:Sort

