{

	Script[EQ2Bot].Variable[Undead_Army]:Set[TRUE]

	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Use Undead_Army",TRUE]:Save

}

else

{

	Script[EQ2Bot].Variable[Undead_Army]:Set[FALSE]

	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Use Undead_Army",FALSE]:Save

}

