}
else
{
	Script[EQ2Bot].Variable[BuffLunge]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Buff Lunge Reversal",FALSE]:Save
}

