Tabcontrol.OnLeftClick:
if ${UIElement[Craft Selection].FindUsableChild[Craft Main,tabcontrol].SelectedTab.Name.Equal[Advanced]}
{
	UIElement[Craft Selection].FindUsableChild[Harvest Frame,frame]:Hide
	UIElement[Craft Selection].FindUsableChild[Additional Options Frame,frame]:Hide
	UIElement[Craft Selection].FindUsableChild[Crafting List Frame,frame]:Show
}
elseif ${UIElement[Craft Selection].FindUsableChild[Craft Main,tabcontrol].SelectedTab.Name.Equal[Options]}
{
	UIElement[Craft Selection].FindUsableChild[Harvest Frame,frame]:Hide
	UIElement[Craft Selection].FindUsableChild[Crafting List Frame,frame]:Hide
	UIElement[Craft Selection].FindUsableChild[Additional Options Frame,frame]:Show
}
elseif ${UIElement[Craft Selection].FindUsableChild[Craft Main,tabcontrol].SelectedTab.Name.Equal[Writs]}
{
	UIElement[Craft Selection].FindUsableChild[Harvest Frame,frame]:Hide
	UIElement[Craft Selection].FindUsableChild[Crafting List Frame,frame]:Hide
	UIElement[Craft Selection].FindUsableChild[Additional Options Frame,frame]:Hide
}	
elseif ${UIElement[Craft Selection].FindUsableChild[Craft Main,tabcontrol].SelectedTab.Name.Equal[About]}
{
	UIElement[Craft Selection].FindUsableChild[Harvest Frame,frame]:Hide
	UIElement[Craft Selection].FindUsableChild[Crafting List Frame,frame]:Hide
	UIElement[Craft Selection].FindUsableChild[Additional Options Frame,frame]:Hide
}														
else
{
	UIElement[Craft Selection].FindUsableChild[Crafting List Frame,frame]:Hide
	UIElement[Craft Selection].FindUsableChild[Additional Options Frame,frame]:Hide
	if ${thisharvestframe}
	{
		UIElement[Craft Selection].FindUsableChild[Harvest Frame,frame]:Show
	}
}

Checkbox.OnLoad:
if ${Script[${If[${Script[Buffer:CraftBeta](exists)},Buffer:CraftBeta,Craft]}].Variable[SortLevel]}
{
	UIElement[Sort Name@Recipes@Craft Main@Main Frame@Craft Selection]:SetChecked
}

if !${Script[${If[${Script[Buffer:CraftBeta](exists)},Buffer:CraftBeta,Craft]}].Variable[SortLevel]}
{
	UIElement[Sort Level@Recipes@Craft Main@Main Frame@Craft Selection]:SetChecked
}

if ${Script[${If[${Script[Buffer:CraftBeta](exists)},Buffer:CraftBeta,Craft]}].Variable[StandardFilter]}
{
	UIElement[Standard Filter@Recipes@Craft Main@Main Frame@Craft Selection]:SetChecked
}

Checkbox.OnLeftClick:
										if ${UIElement[Standard Filter@Recipes@Craft Main@Main Frame@Craft Selection].Checked}
										{
											Script[${If[${Script[Buffer:CraftBeta](exists)},Buffer:CraftBeta,Craft]}].Variable[StandardFilter]:Set[TRUE]
										}
										else
										{
											Script[${If[${Script[Buffer:CraftBeta](exists)},Buffer:CraftBeta,Craft]}].Variable[StandardFilter]:Set[FALSE]
										}

										Script[${If[${Script[Buffer:CraftBeta](exists)},Buffer:CraftBeta,Craft]}]:ExecuteAtom[UpdateSettings]
										
										if !${thisbuttonrefresh}
										{
											thisbuttonrefresh:Set[1]
											Script[${If[${Script[Buffer:CraftBeta](exists)},Buffer:CraftBeta,Craft]}]:QueueCommand[Craft:RefreshList]
										}

