Commandcheckbox.OnLeftClick:
if ${This.Checked}
{
	Script[EQ2Bot].Variable[PBAoEMode]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Cast PB AoE Spells",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[PBAoEMode]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Cast PB AoE Spells",FALSE]:Save
}

if ${This.Checked}
{
	Script[EQ2Bot].Variable[PetMode]:Set[TRUE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Use Special Pet",TRUE]:Save
}
else
{
	Script[EQ2Bot].Variable[PetMode]:Set[FALSE]
	SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[${Me.SubClass}]:Set["Use Special Pet",FALSE]:Save
}
